import { useState } from "react";

const Form = ({ createTodo }) => {
  const [enterdTodo, setEnterdTodo] = useState("");
  const addTodo = () => {
    const newTodo = {
      id: Math.floor(Math.random() * 1e5),
      content: enterdTodo,
    };
    createTodo(newTodo);
  };

  return (
    <div>
      <input
        type="text"
        onChange={(e) => {
          setEnterdTodo(e.target.value);
        }}
      />
      <button onClick={addTodo}>追加</button>
    </div>
  );
};

export default Form;
