  return (
    <>
          <header className={`content-${theme}`}>
        {THEMES.map((_theme) => {
          return (
            <label>
              <input
                type="radio"
                key={_theme}
                value={_theme}
                checked={theme === _theme}
                onChange={changeTheme}
              />
              {_theme}
